import React, {useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {ProgressBar} from 'react-bootstrap';

import Nav from './features/navBar' ;
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { Line } from 'react-chartjs-2';
import Chart from 'chart.js/auto'
import LineChart from './LineChart'








function Home(){
    //usestate of weather data, can be an array
    //useState of soil Moisture data, can also be an array

    const [restAPI, setRestAPI] = useState([]);

    /* form of 
        [temp : 25,
		humidity : 88,
		pressure : 101325,
		description : 'sun',
        weathercode : 200
        soilMoisture : 100
        VBat: 69]

        add picture functionality.
    */
    //const [chartData, setChartData] = useState({})


    //API data with a promise resolve nad useEffect
    useEffect(() => {

        //fetch rest api
        const fetchData =async()=>{
        try {
            let response = await fetch('/api');
            let data = await response.json();
            setRestAPI(data);


        } catch(error){
            console.log(error)
        }


    }
    fetchData()

    },[])

    (async() => {
        const mapData = await restAPI.map((x)=>{
        <li key = {x._id}>{x.temp}</li>
    })
})()
    return(
        <div className="Home">
            <Nav />
            <div className ="progressBar">

                {/* <ProgressBar now = {restAPI?.at(-1).VBat}/> */}
            </div>
            <div>
                <ul>{mapData}</ul>
             {/* <LineChart data = {restAPI}/>    */}
            </div>
        </div>



    );




}
export default Home;
