import React, {useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {ProgressBar} from 'react-bootstrap';

import Nav from './features/navBar' ;
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { Line } from 'react-chartjs-2';
import Chart from 'chart.js/auto'
import LineChart from './LineChart'
import { setDatasets } from 'react-chartjs-2/dist/utils';








function Home(){
    //usestate of weather data, can be an array
    //useState of soil Moisture data, can also be an array

    const [restAPI, setRestAPI] = useState([]);

    /* form of 
        [temp : 25,
		humidity : 88,
		pressure : 101325,
		description : 'sun',
        weathercode : 200
        soilMoisture : 100
        VBat: 69]

        add picture functionality.
    */
    //const [chartData, setChartData] = useState({})


    //API data with a promise resolve nad useEffect
    const [loading, setLoading] = useState(true);
    useEffect(() => {

        const fetchData =async()=>{

            fetch('/api').then(re=>{
                if (re.ok){
                    return re.json()
                }
                throw re
            }).then(data => {
                setRestAPI(data)
               console.log(data)
               console.log(restAPI.map(x=>x.temp))
            }).catch(err =>{
                console.log(err)
            }).finally(()=>{
                setLoading(false);
            })
            // let response = await fetch('/api');
            // let data = await response.json();
            // setRestAPI(data);
            // console.log(restAPI);

        }

    fetchData()

    },[])

    if(loading) return "Loading..."

    return(
        <div className="Home">
            <Nav />
            <div className ="progressBar">

                {/* <ProgressBar now = {restAPI?.at(-1).VBat}/> */}
            </div>
            <div>

             {/* <LineChart data = {restAPI}/>    */}
            </div>
        </div>



    );




}
export default Home;
