import { Link } from 'react-router-dom';
import 'react-bootstrap';
import Nav from './features/navBar' ;
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { Line } from 'react-chartjs-2';
import Chart from 'chart.js/auto'



const options = {
  plugins:{legend:{display:false}},
  layout:{padding:{bottom:100}},
  scales: {
    y:{
      ticks:{
        color:"white",
        font:{
          size:18
        }
      },
      grid:{
        color:"#243240"
      }
    },
    x:{
      ticks:{
        color:"white",
        font:{
          size:18
        }
      }
    }
  },
};


const cooperCityWeatherId = 4151824;
const apiKey = process.env.REACT_APP_WEATHER_KEY;





const WeatherApiUrl = `http://api.openweathermap.org/data/2.5/weather?id=${cooperCityWeatherId}&appid=${apiKey}`

function Home(){
    //usestate of weather data, can be an array
    //useState of soil Moisture data, can also be an array
    const [weatherData, setWeatherData] = useState({main: '', weather : [], temp: []});
    const [soilMoisture, setSoilMoisture] = useState({soilMoisture: ''});
    const [chartData, setChartData] = useState({})


    //API data with a promise resolve nad useEffect
    useEffect(() => {
        let isMounted = true;
        const fetchData = () =>{

            Promise.all([
                fetch(WeatherApiUrl).then((resp)=> resp.json()).then(weather => {
                    if(isMounted){
                        setWeatherData({main : weather.main, weather : weather.weather, temp: [...weatherData.temp, weather.main.temp]})

                    }
                }) 
                //fetch('/api').then(resp => resp.json()).then(moisture => setSoilMoisture(moisture)),
            ]).then()

        }


       fetchData()

        console.log(weatherData.weather[0].description)
       return () => {
    //        cleanup
       isMounted = false; // This worked for me
      };

    },[])


    return(
        <div className="Home">
            <Nav />
            <div>

            <Line data={chartData} options={options} redraw />
            </div>
        </div>



    );




}
export default Home;
