import React, { useState, useEffect } from 'react'
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);







const LineChart = (props) => {
    //console.log(props)
    const [chart, setChart] = useState([])
  const[soilMoisture, setSoilMoisture] = useState([])


  useEffect((err, props)=>{
    async function fetchProps(){
    setChart(await props);
    console.log(chart);
    setSoilMoisture(await props.map(x=> x.soilMoisture))

    }

    fetchProps()
  }, []);


    // useEffect(() => {
    //     const fetchData = async () =>{
    //         /*
    //           new rest format
    //           weatherApi pulls from /api/weather

    //           will pull weather array of past temperature, descriptions, humidity, etc.

    //           lastmeasured temp
    //           lastmeasured description
    //           lastmeasured humidity
    //           last measured data

    //         */





    //         Promise.all([
    //           fetch(WeatherApiUrl).then((resp)=> resp.json()).then(weather => {

    //             setChart(weather)
    //             setTemp([...temp, weather.main.temp])
    //           })
    //             //fetch('/api').then(resp => resp.json()).then(moisture => setSoilMoisture(moisture)),
    //         ]).catch((error) => {
    //             console.log(error);
    //           });
    //         }

    //     fetchData()


    //     }, []);



    //console.log("chart", chart);

    var data = {
      labels: chart?.map(x => x.description),
      datasets: [{
        label: chart?.map(x => x.expirationSet),
        data: chart?.map(x => x.temp),
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)'
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)'
        ],
        borderWidth: 1
      }]
    };

    var moistureData = {
      labels: chart?.map(x => x.expirationSet),
      datasets: [{
        label: chart?.map(x => x.description),
        data: soilMoisture,
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)'
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)'
        ],
        borderWidth: 1
      }]
    };

    var options = {
      maintainAspectRatio: false,
      scales: {
      },
      legend: {
        labels: {
          fontSize: 25,
        },
      },
    }

    return (
      <div><div>

        <Line
          data={data}
          height={400}
          options={options}

        />
      </div>
      <div className= 'moistureGraph'>
        <Line
        data={moistureData}
        height = {400}
        options = {options}
        />

      </div></div>
    )
  }

  export default LineChart
