import React, {useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {ProgressBar} from 'react-bootstrap';

import Nav from './features/navBar' ;
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import LineChart from './LineChart'









function Home(){
    //usestate of weather data, can be an array
    //useState of soil Moisture data, can also be an array

    const [restAPI, setRestAPI] = useState([]);

    /* form of 
        [temp : 25,
		humidity : 88,
		pressure : 101325,
		description : 'sun',
        weathercode : 200
        soilMoisture : 100
        VBat: 69]

        add picture functionality.
    */
    //const [chartData, setChartData] = useState({})


    //API data with a promise resolve nad useEffect
    const [loading, setLoading] = useState(true);
    useEffect(() => {

        const fetchData =async()=>{

            fetch('/api').then(re=>{
                if (re.ok){
                    return re.json()
                }
                throw re
            }).then(data => {
                setRestAPI(data)
               console.log(data)

            }).catch(err =>{
                console.log(err)
            }).finally(()=>{
                setLoading(false);

            })
            // let response = await fetch('/api');
            // let data = await response.json();
            // setRestAPI(data);
            // console.log(restAPI);

        }

    fetchData()

    },[])



    return(
        <div className="Home">
            <Nav />
            <div className ="progressBar">

                {/* <ProgressBar now = {restAPI?.at(-1).VBat}/> */}
            </div>
            <div>

             <LineChart data = {[{"_id":"61dcc9032a3eb901f2a4a843","temp":23.38,"humidity":81,"pressure":1020,"description":"broken clouds","weathercode":803,"rain":0,"soilMoisture":69,"VBat":100},{"_id":"61dcc9325640e06220ba0be4","temp":23.38,"humidity":81,"pressure":1020,"description":"broken clouds","weathercode":803,"rain":0,"soilMoisture":69,"VBat":100},{"_id":"61dcc936f7a210c01fd6f22a","temp":23.35,"humidity":80,"pressure":1020,"description":"clear sky","weathercode":800,"rain":0,"soilMoisture":69,"VBat":100},{"_id":"61dcc984fc46de8f2e7127a2","temp":23.38,"humidity":81,"pressure":1020,"description":"broken clouds","weathercode":803,"rain":0,"soilMoisture":69,"VBat":100}]}/>   
            </div>
        </div>



    );




}
export default Home;
