    let copy = bigArray;
    chart.data.map(x => {

        x.entry.map(y=> {

            let exists = findIfIdExists(y._id, copy);
            // console.log(exists);
            // console.log(y);
            // console.log(bigArray)
            if(exists == -1) {
                copy.push([y]);
                console.log(copy)


            }
