        labels: {
          fontSize: 25,
        },
      },
    }


    return (
      <div>
        <br />
        <br />
        <div className='moistureGraph'>
          <Line
            data={moistureData}
            height={400}
            options={options}
          />

        </div></div>
    )
  }

