            console.log(data)
            setRestAPI(data);

        } catch(error){
            console.log(error)
        }


    }


    },[])


    return(
        <div className="Home">
            <Nav />
            <div className ="progressBar">
                {/* <ProgressBar now = {restAPI?.at(-1).VBat}/> */}
            </div>
            <div>
             <LineChart data = {restAPI}/>   
            </div>
        </div>



    );




}
export default Home;
