      <div><div>

        <Line
          data={data}
          height={400}
          options={options}

        />
      </div>
      <div className= 'moistureGraph'>
        <Line
        data={moistureData}
        height = {400}
        options = {options}
        />

      </div></div>
    )
  }

  export default LineChart
