    //API data with a promise resolve nad useEffect
    const [loading, setLoading] = useState(true);
    const [err, setError] = useState(null)
    useEffect(() => {

        const fetchData =async()=>{

            fetch('/api').then(re=>{
                if (re.ok){
                    return re.json()
                }
                throw re
            }).then(data => {
                setRestAPI(data)
               console.log(data)

            }).catch(err =>{
                console.log(err)
                setError(err)
            }).finally(()=>{
                setLoading(false);

            })


        }

    fetchData()


    },[])

    if(err) return "error";

    return(
        <div className="Home">
            <Nav />

            <div>

             <LineChart data = {[{"_id":"61dce0c540713b17a109e89b","temp":22.62,"humidity":82,"pressure":1021,"description":"clear sky","weathercode":800,"rain":0,"soilMoisture":69,"VBat":100,"expirationSet":"2022-01-11T01:43:33.198Z"}]}/>   
            </div>
        </div>



    );




}
export default Home;
