        const fetchData = () =>{

            Promise.all([
                fetch(WeatherApiUrl).then(resp => resp.json()).then(weather => setWeatherData({main : weather.main, weather : weather.weather})), 
                fetch('/api').then(resp => resp.json()),
            ])

        }
       fetchData()

    },[])

    return(
        <div className="Home">
            <Nav />
            {weatherData.main}
        </div>



    );




}
export default Home;
