import React, {useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {ProgressBar} from 'react-bootstrap';

import Nav from './features/navBar' ;
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import LineChart from './LineChart'









function Home(){
    //usestate of weather data, can be an array
    //useState of soil Moisture data, can also be an array

    const [restAPI, setRestAPI] = useState([]);

    /* form of 
        [temp : 25,
		humidity : 88,
		pressure : 101325,
		description : 'sun',
        weathercode : 200
        soilMoisture : 100
        VBat: 69]

        add picture functionality.
    */
    //const [chartData, setChartData] = useState({})


    //API data with a promise resolve nad useEffect
    const [loading, setLoading] = useState(true);
    const [err, setError] = useState(null)
    useEffect(() => {

        const fetchData =async()=>{

            fetch('/api').then(re=>{
                if (re.ok){
                    return re.json()
                }
                throw re
            }).then(data => {
                setRestAPI(data)
               console.log(data)

            }).catch(err =>{
                console.log(err)
                setError(err)
            }).finally(()=>{
                setLoading(false);

            })


        }

    fetchData()


    },[])

    if(err) return "error";
    console.log(restAPI.at(-1).VBat)
    return(
        <div className="Home">
            <Nav />
            <div className ="progressBar">
                Battery Voltage
                <ProgressBar now = {restAPI?.at(-1)?.VBat}/>
                percentage: {restAPI.at(-1).VBat}
            </div>
            <div>

             <LineChart data = {restAPI}/>   
            </div>
        </div>



    );




}
export default Home;
